def get_attribute_constraints(attribute_name):
    if attribute_name == "Country":
        return { "fields": { "column": { "type": "string", "min_length": 4, "max_length": 52, "max_nulls": 10000 } } } 
    elif attribute_name == "Year":
        return { "fields": { "column": { "type": "int", "min": 1995, "max": 2011, "sign": "positive", "max_nulls": 10000 } } } 
    elif attribute_name == "Count":
        return { "fields": { "column": { "type": "int", "min": 0, "max": 45559, "sign": "non-negative", "max_nulls": 10000 } } }        
    elif attribute_name == "Rate":
        return { "fields": { "column": { "type": "real", "min": 0.0, "max": 139.1, "sign": "non-negative", "max_nulls": 10000 } } }         
    elif attribute_name == "Source":
        return { "fields": { "column": { "type": "string", "min_length": 3, "max_length": 28, "max_nulls": 10000 } } }      
    elif attribute_name == "Source Type":
        return { "fields": { "column": { "type": "string", "min_length": 2, "max_length": 2, "max_nulls": 10000, "allowed_values": [ "CJ", "PH" ] } } } 
    else:
        return { "fields": { "column": { "type": "int", "min": 1995, "max": 2011, "sign": "positive", "max_nulls": 10000 } } } 

